var mapper = function(lst1, lst2) {
	var lesser = lst1;
	var lesser_len = lst1.length;
	var greater_len = lst2.length;
	var newLst = [];

	if (lst1.length > lst2.length) {
		lesser = lst2;
		lesser_len = lst2.length;
		greater_len = lst1.length;
	}

	var curr = 0;
	var lastNumCount = 0;
	for(var i = 0; i < greater_len; i++) {
		if ((Math.random() < (lesser_len/greater_len) || lastNumCount > (greater_len/lesser_len)) && curr < lesser_len - 1) {
			curr ++;
			lastNumCount = -1;
		}
		newLst.push(lesser[curr]);
		lastNumCount ++;
	}

	newLst[0] = lesser[0];
	newLst[greater_len-1] = lesser[lesser_len-1];

	return newLst;
}

