var makeWard = function(coords, color, map) {
	return new google.maps.Polygon({
		paths: coords,
		strokeColor: color,
		strokeOpacity: 0.8,
		strokeWeight: 0.7,
		fillColor: color,
		fillOpacity: .3,
		map: map
	});
}

